-- P10: Patent Mastery for Indian Startups (Enhanced)
-- 60 days, 12 modules, comprehensive IP strategy with India-specific data
-- IP India fee structures, timelines, expedited examination, PCT national phase

BEGIN;

DO $$
DECLARE
    v_product_id TEXT;
    v_mod_1_id TEXT;
    v_mod_2_id TEXT;
    v_mod_3_id TEXT;
    v_mod_4_id TEXT;
    v_mod_5_id TEXT;
    v_mod_6_id TEXT;
    v_mod_7_id TEXT;
    v_mod_8_id TEXT;
    v_mod_9_id TEXT;
    v_mod_10_id TEXT;
    v_mod_11_id TEXT;
    v_mod_12_id TEXT;
BEGIN
    -- Generate IDs
    v_product_id := gen_random_uuid()::text;
    v_mod_1_id := gen_random_uuid()::text;
    v_mod_2_id := gen_random_uuid()::text;
    v_mod_3_id := gen_random_uuid()::text;
    v_mod_4_id := gen_random_uuid()::text;
    v_mod_5_id := gen_random_uuid()::text;
    v_mod_6_id := gen_random_uuid()::text;
    v_mod_7_id := gen_random_uuid()::text;
    v_mod_8_id := gen_random_uuid()::text;
    v_mod_9_id := gen_random_uuid()::text;
    v_mod_10_id := gen_random_uuid()::text;
    v_mod_11_id := gen_random_uuid()::text;
    v_mod_12_id := gen_random_uuid()::text;

    -- Insert/Update Product
    VALUES (
        v_product_id,
        'P10',
        'Patent Mastery for Indian Startups',
        'Master intellectual property from filing to monetization - 12 modules covering patent fundamentals, patentability assessment, prior art searches, patent drafting, Indian patent filing process, PCT international applications, patent prosecution, portfolio management, licensing, infringement enforcement, trade secrets, and startup IP strategy. Navigate IP India procedures with 80% fee discounts for startups.',
        7999,
        60,
        NOW(),
        NOW()
    )
    ON CONFLICT (code) DO UPDATE SET
        title = EXCLUDED.title,
        description = EXCLUDED.description,
        price = EXCLUDED.price,
        "estimatedDays" = EXCLUDED."estimatedDays",
        "updatedAt" = NOW();

    -- Get the product ID (in case of conflict)
    SELECT id INTO v_product_id FROM "Product" WHERE code = 'P10';

    -- Clean existing modules and lessons for this product
    DELETE FROM "Lesson" WHERE "moduleId" IN (SELECT id FROM "Module" WHERE "productId" = v_product_id);
    DELETE FROM "Module" WHERE "productId" = v_product_id;

    -- Module 1: Patent Fundamentals & Indian IP Landscape (Days 1-5)
    VALUES (v_mod_1_id, v_product_id, 'Patent Fundamentals & Indian IP Landscape', 'Master patent basics, Indian Patents Act 1970, and the evolving IP ecosystem that offers significant advantages to startups', 1, NOW(), NOW());


    -- Module 2: Prior Art Search Strategies (Days 6-10)
    VALUES (v_mod_2_id, v_product_id, 'Prior Art Search Strategies', 'Master comprehensive prior art search techniques using Indian and international patent databases to strengthen your applications', 2, NOW(), NOW());


    -- Module 3: Patent Drafting Mastery (Days 11-15)
    VALUES (v_mod_3_id, v_product_id, 'Patent Drafting Mastery', 'Learn to draft patent applications that survive examination and provide strong protection in the Indian context', 3, NOW(), NOW());


    -- Module 4: Indian Patent Filing Process (Days 16-20)
    VALUES (v_mod_4_id, v_product_id, 'Indian Patent Filing Process', 'Navigate the IP India e-filing system, forms, fees, and procedures with 80% fee discount for recognized startups', 4, NOW(), NOW());


    -- Module 5: PCT International Applications (Days 21-25)
    VALUES (v_mod_5_id, v_product_id, 'PCT International Applications', 'Master the Patent Cooperation Treaty system for cost-effective international patent protection with India as receiving office', 5, NOW(), NOW());


    -- Module 6: Patent Prosecution (Days 26-30)
    VALUES (v_mod_6_id, v_product_id, 'Patent Prosecution', 'Navigate examination, respond to objections, and secure patent grant through the Indian Patent Office', 6, NOW(), NOW());


    -- Module 7: Patent Portfolio Management (Days 31-35)
    VALUES (v_mod_7_id, v_product_id, 'Patent Portfolio Management', 'Build, maintain, and optimize your patent portfolio for maximum business value and competitive advantage', 7, NOW(), NOW());


    -- Module 8: Patent Licensing and Monetization (Days 36-40)
    VALUES (v_mod_8_id, v_product_id, 'Patent Licensing and Monetization', 'Generate revenue from patents through licensing, sale, and strategic deployment in the Indian market', 8, NOW(), NOW());


    -- Module 9: Patent Infringement and Enforcement (Days 41-45)
    VALUES (v_mod_9_id, v_product_id, 'Patent Infringement and Enforcement', 'Protect your patents through monitoring, assertion, and enforcement in Indian courts', 9, NOW(), NOW());


    -- Module 10: Trade Secrets and Confidential Information (Days 46-50)
    VALUES (v_mod_10_id, v_product_id, 'Trade Secrets and Confidential Information', 'Protect valuable business information through trade secret strategy when patents are not appropriate', 10, NOW(), NOW());


    -- Module 11: Design Patents and Utility Models (Days 51-55)
    VALUES (v_mod_11_id, v_product_id, 'Design Patents and Utility Models', 'Leverage design registration for aesthetic innovation and understand utility model systems in other jurisdictions', 11, NOW(), NOW());


    -- Module 12: Startup IP Strategy and Best Practices (Days 56-60)
    VALUES (v_mod_12_id, v_product_id, 'Startup IP Strategy and Best Practices', 'Develop comprehensive IP strategy aligned with startup lifecycle from founding through growth and exit', 12, NOW(), NOW());


END $$;

COMMIT;
